<script type="text/javascript">
var waittime = 100; /* time between chat refreshes (ms) */
var myTimeout = window.setTimeout("read_cont();", waittime);

//$("#form-chat-id").click(function(){var myTimeout = utoUpdate();})

  function AjaxInsertChat()
  {
    clearTimeout(myTimeout);
	var source = document.getElementById('box_text');
	if ($.trim(source.value)){
	$( document ).ajaxSend(function() {
  	});
	var data = {};
    data['textc']     =    source.value;
	data['ajax']    =    1;
	$.post("index.php?page=chat_message&start=1",data,
	function Success(xml) {
	source.value = "";
	$("div.chat_ee" ).scrollTop( 10000 );
    });
	} else {
	alert("{$lang['Please_write_the_message']}");
	}
 	var myTimeout = window.setTimeout("read_cont();", waittime);
  }

function utoUpdate(url) {
  $.get(url, function(utodata) {
    $("#content_div_id").html(utodata);
    $("#content_online").html();
    var myTimeout = window.setTimeout("read_cont();", waittime);
  });

}

function read_cont() {
	zeit = new Date();
	ms = (zeit.getHours() * 24 * 60 * 1000) + (zeit.getMinutes() * 60 * 1000) + (zeit.getSeconds() * 1000) + zeit.getMilliseconds();
	utoUpdate("index.php?page=chat_message&chat_w=1&x=" + ms);
}

$("#form-chat-id").on("keypress", function (event) {
            console.log("aaya");
            var keyPressed = event.keyCode || event.which;
            if (keyPressed === 13) {
                AjaxInsertChat();
                event.preventDefault();
                return false;
            }
        });


</script>

</body>
