<script type="text/javascript">
// Initialize the widget when the DOM is ready
$(function() {
$("#filelist").plupload({
// General settings
runtimes : 'html5,flash,silverlight,html4',
url : '{$ForumAdress}includes/upload.php',
unique_names: true,
// User can upload no more then 20 files in one go (sets multiple_queues to false)
max_file_count: 1,

chunk_size: '1mb',

// Resize images on clientside if we can
resize : {
width : 700,
height : 300,
quality : 90,
crop: true // crop to exact dimensions
},

filters : {
// Maximum file size
max_file_size : '1mb',
// Specify what files to browse for
mime_types: [
{title : "Image files", extensions : "jpg,gif,png,jpeg"}
]
},

// Rename files by clicking on their titles
rename: true,

// Sort files
sortable: true,
prevent_duplicates: true,
init : {
FilesAdded: function(up, files) {
var max_files = 1;
plupload.each(files, function(file) {
if (up.files.length > max_files) {
alert("{$lang['cover_photo_chaos_single_file']}");
up.removeFile(file);
}
});
if (up.files.length >= max_files) {
$('#pickfiles').hide();
}
},
FilesRemoved: function(up, files) {
if (up.files.length < max_files) {
$('#pickfiles').fadeIn();
}
}
},
// Enable ability to drag'n'drop files onto the widget (currently only HTML5 supports that)
dragdrop: true,

// Views to activate
views: {
list: false,
thumbs: true, // Show thumbs
active: 'thumbs'
},

// Flash settings
flash_swf_url : '{$ForumAdress}applications/core/plupload/Moxie.swf',

// Silverlight settings
silverlight_xap_url : '{$ForumAdress}applications/core/plupload/Moxie.xap'
});


});
</script>

