<script type="text/javascript">
$(document).ready(function() {
    $("#upload_link").on('click', function(e){
        e.preventDefault();
        $("#files").trigger('click');
    });

});

function uploadFile() {

	$.ajax({
	// Your server script to process the upload
	url: "index.php?page=attachments&start=1&subject_id={$subject_id}&reply_id={$reply_id}",
	type: 'POST',
	// Form data
	data: new FormData($('#uploadForm')[0]),
	// Tell jQuery not to process data or worry about content-type
	// You *must* include these options!
	cache: false,
	contentType: false,
	processData: false,
    success: function (xml)
    {
     var AjaxState = {ajaxSend : $("#message").html("<img alt='' class='brd0' src='{$image_path}/loading.gif'>")};
	 $("#message").html(xml);
	 $('#targetood').hide();
	 },
	// Custom XMLHttpRequest
	xhr: function () {
	var myXhr = $.ajaxSettings.xhr();
	$('#progress-div').show();
	$("#progress-bar").width('0%');
	if (myXhr.upload) {
	// For handling the progress of the upload
	myXhr.upload.addEventListener('progress', function (e) {
	if (e.lengthComputable) {
	var percentComplete = parseInt((e.loaded / e.total) * 100);
	$("#progress-bar").width(percentComplete + '%');
	$("#progress-bar").html('<div id="progress-status">' + percentComplete +' %</div>');
	if (percentComplete == 100) {
	$('#progress-div').hide("slow");
	}
	}
	}, false);
	}
	return myXhr;
	}
	});

}

function DeletAttach(id, subject_id) {
	$.ajax({
	// Your server script to process the upload
	url: "index.php?page=attachments&delete_attach=1&id="+id+"&subject_id="+subject_id+"&reply_id={$reply_id}",
	type: 'POST',
	// Form data
	data: new FormData($('#uploadForm')[0]),
	// Tell jQuery not to process data or worry about content-type
	// You *must* include these options!
	cache: false,
	contentType: false,
	processData: false,
    success: function (xml)
    {
     var AjaxState = {ajaxSend : $("#message").html("<img alt='' class='brd0' src='{$image_path}/loading.gif'>")};
	 $("#message").html(xml);
	 $('#targetood').hide();
	 },
	 // Custom XMLHttpRequest
	xhr: function () {
	var myXhr = $.ajaxSettings.xhr();
	$('#progress-div').show();
	$("#progress-bar").width('0%');
	if (myXhr.upload) {
	// For handling the progress of the upload
	myXhr.upload.addEventListener('progress', function (e) {
	if (e.lengthComputable) {
	var percentComplete = parseInt((e.loaded / e.total) * 100);
	$("#progress-bar").width(percentComplete + '%');
	$("#progress-bar").html('<div id="progress-status">' + percentComplete +' %</div>');
	if (percentComplete == 100) {
	$('#progress-div').hide("slow");
	}
	}
	}, false);
	}
	return myXhr;
	}
	});

}

 function slide_Down(id) {
 $("#panel_"+id).slideToggle("slow");
}

function getMeta(url) {
	$("<img/>",{
	load: function() {
	if (this.width > {$_CONF['info_row']['default_imagesW']}) {
	window.parent.PBBEditor.wysiwygEditorInsertHtml('<img src="'+url+'" width="{$_CONF['info_row']['default_imagesW']}" height="{$_CONF['info_row']['default_imagesH']}" />');
	}
	else
	{
	window.parent.PBBEditor.wysiwygEditorInsertHtml('<img src="'+url+'" width="'+this.width+'" height="'+this.height+'" />');
	}
	},
	src: url
	});
}
</script>

